import { useState } from "react";
import maleProfile from "./images/maleProfile.jpg";
import femaleProfile from "./images/femaleProfile.jpg";

const Employees = () => {
  const [selectedTeam, setTeam] = useState("TeamB");

  const [employees, setEmployees] = useState([
    {
      id: 1,
      fullName: "Moldogazy Kabylbekov",
      designation: "JavaScript Developer",
      gender: "male",
      teamName: "GroupA",
    },
    {
      id: 2,
      fullName: "Bakai Akylbekov",
      designation: "VueJS Developer",
      gender: "male",
      teamName: "GroupA",
    },
    {
      id: 3,
      fullName: "Erbol Tukashov",
      designation: "ReactJS Developer",
      gender: "male",
      teamName: "GroupA",
    },
    {
      id: 4,
      fullName: "Nasyikat Mambetalieva",
      designation: "Web Desiner",
      gender: "female",
      teamName: "GroupB",
    },
    {
      id: 5,
      fullName: "Ainagul Shabdanova",
      designation: "JavaScript Developer",
      gender: "female",
      teamName: "GroupB",
    },
    {
      id: 6,
      fullName: "Myrzaiym Askarova",
      designation: "VeuJS Developer",
      gender: "female",
      teamName: "GroupB",
    },
    {
      id: 7,
      fullName: "Zhyldyzback Shatmanov",
      designation: "ReactJS Developer",
      gender: "male",
      teamName: "GroupC",
    },
    {
      id: 8,
      fullName: "Peri Melisova",
      designation: "Web Desiner",
      gender: "feale",
      teamName: "GroupC",
    },
    {
      id: 9,
      fullName: "Max Asanbekov",
      designation: "SQL Developer",
      gender: "male",
      teamName: "GroupC",
    },
    {
      id: 10,
      fullName: "Mark Zuckerberg",
      designation: "Node Developer",
      gender: "male",
      teamName: "GroupD",
    },
    {
      id: 11,
      fullName: "Azamat Zhamankulov",
      designation: "Product Manager",
      gender: "male",
      teamName: "GroupD",
    },
    {
      id: 12,
      fullName: "Jalyn Kyrgyz uulu",
      designation: "Kotlin Developer",
      gender: "male",
      teamName: "GroupD",
    },
  ]);

  function handleTeamSeclectionChange(e) {
    setTeam(e.target.value);
  }

  function handleEmployeeCardClick(e) {
    const transformedEmployees = employees.map((employee) =>
      employee.id === parseInt(e.currentTarget.id)
        ? employee.teamName === selectedTeam
          ? { ...employee, teamName: "" }
          : { ...employee, teamName: selectedTeam }
        : employee
    );

    setEmployees(transformedEmployees);
  }

  return (
    <main className="container">
      <div className="row justify-content-center mt-3 mb-3">
        <div className="col-8">
          <select
            className="form-select form-select-lg"
            value={selectedTeam}
            onChange={handleTeamSeclectionChange}
          >
            <option value="TeamA">TeamA</option>
            <option value="TeamB">TeamB</option>
            <option value="TeamC">TeamC</option>
            <option value="TeamD">TeamD</option>
          </select>
        </div>
      </div>
      <div className="row justify-content-center mt-3 mb-3">
        <div className="col-8">
          <div className="card-collection">
            {employees.map((employee) => (
              <div
                id={employee.id}
                className="card m-2"
                style={{ cursor: "pointer " }}
                onClick={handleEmployeeCardClick}
              >
                {employee.gender === "male" ? (
                  <img
                    className="card-img-top"
                    src={maleProfile}
                    alt="Female Prifile"
                  />
                ) : (
                  <img
                    className="card-img-top"
                    src={femaleProfile}
                    alt="Female Prifile"
                  />
                )}
                <div className="card-body">
                  <h5 className="card-title">Full Name: {employee.fullName}</h5>
                  <p className="card-tetx">
                    <strong>Designation</strong> {employee.designation}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </main>
  );
};
export default Employees;
